    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            
            listinwarehouse DB = new listinwarehouse();
            DB.createNewProduct(jTextField1.getText(), jTextField2.getText(), Integer.parseInt(jTextField3.getText()), Integer.parseInt(jTextField4.getText()), Integer.parseInt(jTextField5.getText()), (0));
            JOptionPane.showMessageDialog(null, "Product data successfully created");
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(null, "Input error");
        }
       
    }//GEN-LAST:event_jButton1ActionPerformed

